import axios from "axios";

const url = 'https://social-network.samuraijs.com/api/1.0/'

const instance = axios.create({
    withCredentials: true
});

export const getUserApi = (currentPage, pageSize) => {
    return axios.get(url + `users?page=${currentPage}&count=${pageSize}`, {withCredentials: true})
        .then(r => {
            return r.data
        })
};

export const postUserApi= (id) => {
    return axios.post(url + `follow/${id}`, {}, {withCredentials: true, headers: {"API-KEY": "fbf5b600-2f44-40fb-bc63-1e40c8ffb8fc"}})
        .then(r => {
            return r.data
        })
};

export const getProfileApi = (userId) => {
    return axios.get(url + `profile/` + userId)
        .then(r => {
            return r.data
        })
};

export const getLoginApi = () => {
    return axios.get(url + `auth/me`, {withCredentials: true})
        .then(r => {
            return r.data
        })
}
