import message from './Dialogs.module.css';
import DialogItem from './DialogItem/DialogsItem';
import Message from "./MessagesItem/Message";
import {createRef} from "react";
import {sendMessageActionCreater, updMessageActionCreater} from "../../redux/state";


function Dialogs(props) {

    let dialogsElements =
        props.stateDialogs.dialogsData.map( d => <DialogItem name={d.name} id={d.id}/>)
    let messagesElements =
        props.stateDialogs.messagesData.map( m => <Message ava={m.ava} content={m.message} id={m.id}/>)

    let newMessageElement = createRef();
    let sendMessage = () => {
        props.dispatch(sendMessageActionCreater());
        newMessageElement.current.value = '';
    }

    let messageSend = () => {
        let textMessage = newMessageElement.current.value;
        let action = updMessageActionCreater(textMessage);
        props.dispatch(action);
    }


    return(
        <div className={message.dialogs}>
            <div className={message.notify}>
                {dialogsElements}
            </div>
            <div className={message.paper_dial}>
                {messagesElements}
            </div>
            <div>
                <textarea onChange={messageSend} ref={newMessageElement} value={props.newMessageText}/>
            </div>
            <div>
                <button onClick={sendMessage}>Send Message</button>
            </div>
        </div>
    );
}

export default Dialogs
