// actions
const FOLLOW_USER = 'FOLLOW_USER';
const UNFOLLOW_USER = 'UNFOLLOW_USER';
const SHOW_MORE_LOAD = 'SHOW_MORE_LOAD';
const SET_USERS = 'SET_USERS';


// state
let initialState = {
    users: [
        {
        id: '1',
        followed: false,
        name: 'Dimych',
        location: {country: 'Russia', city: 'Moscow'},
        descriptionProfile: 'Сегодня ты делаешь код, завтра код делает тебе деньги'
    }, {
        id: '1',
        followed: true,
        name: 'Evgeniy',
        location: {country: 'Belarus', city: 'Minsk'},
        descriptionProfile: 'Настоящий программист гораздо больше читает, чем пишет'
    }, {
        id: '1',
        followed: true,
        name: 'Sasha',
        location: {country: 'Kazakhstan', city: 'Almaty'},
        descriptionProfile: 'Идеальному коду место в музее: там ценят всякие древности'
    }, {
        id: '1',
        followed: false,
        name: 'Nikita',
        location: {country: 'Uzbekistan', city: 'Tashkent'},
        descriptionProfile: 'Можно сделать довольно большой проект, вообще не прикасаясь к мыши. Но зачем?'
    }]
}

//reducer
const usersReducer = (state = initialState, action) => {
    switch (action.type) {
        case FOLLOW_USER:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id == action.userId) {
                        return {...u, followed: true}
                    }
                })
            }
        case UNFOLLOW_USER:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.userId == action.userId) {
                        return {...u, followed: false}
                    }
                })
            }
        case SET_USERS:
            return {
                ...state,
                users: [...state.users, ...action.users]
            }

        case SHOW_MORE_LOAD:
            return {
                ...state
            }
        default:
            return state
    }
}

// actionCreater
export const followAC = (userId) => ({type: FOLLOW_USER, userId})
export const unFollowAC = (userId) => ({type: UNFOLLOW_USER, userId})
export const setUsersAC = (users) => ({type: SET_USERS, users})

export const ShowMoreAC = () => ({type: SHOW_MORE_LOAD})


export default usersReducer
