import styles from './Users.module.css'
import {useState} from 'react'
import cn from 'classnames'


const Pagination = ({totalUsersCount, pageSize, currentPage, onPageChanged, portionSize = 15 }) => {
    let pagesCount = Math.ceil(totalUsersCount / pageSize)
    let pages = [];
    for (let i = 1; i <= pagesCount; i++) {
        if (pages.length < 15) {
            pages.push(i);
        }
    }

    const portionCount = Math.ceil(pagesCount / portionSize)
    const [numberPortion, setNumberPortion] = useState(1)
    const rightBorder = (numberPortion - 1) * portionSize + 1
    const lefttBorder = numberPortion * portionSize

    return (
        <div className={styles.num_bar}>
            {numberPortion > 1 && <button onClick={() => {setNumberPortion(numberPortion - 1)}}>Prev</button>}
            {pages
                .filter(p => p >= lefttBorder && p <= rightBorder)
                .map(p => <span className={ cn({[styles.selected]: currentPage === p}) } key={p} onClick={(e) => onPageChanged(p) }> {p} </span>) }
            {numberPortion < portionCount && <button onClick={() => {setNumberPortion(numberPortion + 1)}}>Next</button>}
        </div>
    )
}

export default Pagination
