import s from './App.module.css'
import Header from "./compo/Header/Header";
import Nav from "./compo/Nav/Nav";
import Profile from "./compo/Profile/Profile";
import {BrowserRouter, Routes, Route} from 'react-router-dom';
import DialogsContainer from "./compo/Message/DialogsContainer";
import ScrollToTop from "react-scroll-to-top";
import BtnToTop from "./compo/ToTop/btnToTop";

function App(props) {

    return (
        <BrowserRouter>
            <div className={s.grid}>
                <Header/>
                <Nav />
                {/*<BtnToTop/>*/}
                <ScrollToTop smooth component={<p style={{
                    position: "fixed",
                    bottom:"40px",
                    right:"40px",
                    color:"#44444",
                    borderRadius: "3px",
                    lineHeight: "40px",


                }}>â†‘</p>} />
                <div className={s.content}>
                    <Routes>
                        <Route path="/dialogs/*" element={<DialogsContainer  />}/>
                        <Route path="/profile"
                               element={<Profile />}/>
                    </Routes>
                </div>
            </div>
        </BrowserRouter>
    );
}

export default App;
