import message from './Dialogs.module.css';
import DialogItem from './DialogItem/DialogsItem';
import Message from "./MessagesItem/Message";
import {createRef} from "react";



function Dialogs(props) {

    let state = props.dialogsPage;

    let dialogsElements =
        state.dialogsData.map( d => <DialogItem name={d.name} id={d.id}/>)
    let messagesElements =
        state.messagesData.map( m => <Message ava={m.ava} content={m.message} id={m.id}/>)
    let newMessageText = state.newMessageText;

    let newMessageElement = createRef();

    let OnClickSendMessage = () => {
        props.sendMessage();
    }

    let OnNewMessageSend = (e) => {
        let body = e.target.value;
        props.OnNewMessageSend(body);
    }

    return(
        <div className={message.dialogs}>
            <div className={message.notify}>
                {dialogsElements}
            </div>
            <div className={message.paper_dial}>
                {messagesElements}
            </div>
            <div>
                <textarea  onChange={OnNewMessageSend} ref={newMessageElement} value={newMessageText} placeholder={"Enter your message"}/>
            </div>
            <div>
                <button onClick={OnClickSendMessage}>Send Message</button>
            </div>
        </div>
    );
}

export default Dialogs
