import axios from "axios";

const url = 'https://social-network.samuraijs.com/api/1.0/'

const instance = axios.create({
    withCredentials: true,
    headers: {"API-KEY": "fbf5b600-2f44-40fb-bc63-1e40c8ffb8fc"}
});

export const getUserApi = (currentPage, pageSize) => {
    return instance.get(url + `users?page=${currentPage}&count=${pageSize}`)
        .then(r => {
            return r.data
        })
};

export const postUserApi= (id) => {
    return instance.post(url + `follow/${id}`, {})
        .then(r => {
            return r.data
        })
};

export const deleteUserApi = (id) => {
    return instance.delete(url + ``)
}

export const getProfileApi = (userId) => {
    return instance.get(url + `profile/` + userId)
        .then(r => {
            return r.data
        })
};

export const getLoginApi = () => {
    return instance.get(url + `auth/me`)
        .then(r => {
            return r.data
        })
}
