const DialogsContainer = (props) => {

    let navigator = useNavigate();
    useEffect(() => {
        if (props.isAuth === false) {
            return navigator('/login')
        }
    }, [navigator, props.isAuth]);

    return (
        <Dialogs dialogsPage={props.dialogsPage} isAuth={props.isAuth} SendMessageActionCreater={props.SendMessageActionCreater} UpdateNewMessageActionCreater={props.UpdateNewMessageActionCreater}/>
    )
}

const mapStateToProps = (state) => {
    return {
        dialogsPage: state.dialogsPage,
        isAuth: state.auth.isAuth
    }
}

connect(mapStateToProps, {SendMessageActionCreater, UpdateNewMessageActionCreater})(DialogsContainer);

export default DialogsContainer;
