import axios from "axios";

export const getUserApi = (currentPage, pageSize) => {
    return axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${currentPage}&count=${pageSize}`, {withCredentials: true})
        .then(r => {
            return r.data
        })
}

export const postUserApi= (id) => {
    return axios.post(`https://social-network.samuraijs.com/api/1.0/follow/${id}`, {}, {withCredentials: true, headers: {"API-KEY": "fbf5b600-2f44-40fb-bc63-1e40c8ffb8fc"}})
        .then(r => {
            return r.data
        })
}
