const UPDATE_NEW_MESSAGE_TEXT = 'UPDATE_NEW_MESSAGE_TEXT';
const SEND_MESSAGE = 'SEND_MESSAGE';

let initialState = {
    dialogsData: [{id: 1, name: "Dimych",}, {
        id: 2,
        ava: 'https://avatars.mds.yandex.net/i?id=a7190ac7b0d5cc3862b9e0113bebd4d8-4318341-images-thumbs&n=13',
        name: "Evgeniy",
    }, {
        id: 3,
        ava: 'https://avatars.mds.yandex.net/i?id=a7190ac7b0d5cc3862b9e0113bebd4d8-4318341-images-thumbs&n=13',
        name: "Sasha",
    },],
    messagesData: [
        {
            id: 1,
            ava: "https://avatars.mds.yandex.net/i?id=a7190ac7b0d5cc3862b9e0113bebd4d8-4318341-images-thumbs&n=13",
            message: "Hello",
        }, {
            id: 2,
            ava: "https://avatars.mds.yandex.net/i?id=a7190ac7b0d5cc3862b9e0113bebd4d8-4318341-images-thumbs&n=13",
            message: "How are you?",
        }, {
            id: 3,
            ava: "https://avatars.mds.yandex.net/i?id=a7190ac7b0d5cc3862b9e0113bebd4d8-4318341-images-thumbs&n=13",
            message: "Chel",
        }, {
            id: 4,
            ava: "https://avatars.mds.yandex.net/i?id=a7190ac7b0d5cc3862b9e0113bebd4d8-4318341-images-thumbs&n=13",
            message: "REACT REACT AND REDUX",
        },],
    newMessageText: ''
};

const dialogsReducer = (state = initialState, action) => {
    switch (action.type) {
        case UPDATE_NEW_MESSAGE_TEXT:
            return {
                ...state.messagesData,
                ...state.dialogsData,
                newMessageText: action.body,
            }

        case SEND_MESSAGE:
            let body = {
                id: '5', message: state.newMessageText,
            }
            return {
                ...state.messagesData,
                ...state.dialogsData,
                messagesData: [...state.messagesData, body],
                newMessageText: ''
            }
            stateCopy.messagesData.push(body);
        default:
            return state
    }
}

export const SendMessageActionCreater = () => {
    return {
        type: SEND_MESSAGE,
    }
}
export const UpdateNewMessageActionCreater = (body) => {
    return {
        type: UPDATE_NEW_MESSAGE_TEXT, body: body,
    }
}

export default dialogsReducer;
