/*import {rerenderEntireTree} from "../index"; */

let rerenderEntireTree = () => {
    console.log('it is it')
}

let store = () => {
    let state = {
        dialogsPage: {
            dialogsData: [
                {id: 1, name: "Dimych",},
                {
                    id: 2,
                    ava: 'https://avatars.mds.yandex.net/i?id=a7190ac7b0d5cc3862b9e0113bebd4d8-4318341-images-thumbs&n=13',
                    name: "Evgeniy",
                },
                {
                    id: 3,
                    ava: 'https://avatars.mds.yandex.net/i?id=a7190ac7b0d5cc3862b9e0113bebd4d8-4318341-images-thumbs&n=13',
                    name: "Sasha",
                },
            ],
            messagesData: [
                {
                    id: 1,
                    ava: "https://avatars.mds.yandex.net/i?id=a7190ac7b0d5cc3862b9e0113bebd4d8-4318341-images-thumbs&n=13",
                    message: "Hello",
                },
                {
                    id: 2,
                    ava: "https://avatars.mds.yandex.net/i?id=a7190ac7b0d5cc3862b9e0113bebd4d8-4318341-images-thumbs&n=13",
                    message: "How are you?",
                },
                {
                    id: 3,
                    ava: "https://avatars.mds.yandex.net/i?id=a7190ac7b0d5cc3862b9e0113bebd4d8-4318341-images-thumbs&n=13",
                    message: "Chel",
                },
            ],
        },
        profilePage: {
            postsData: [
                {name: 'Evgeniy', text: 'I need more React', like: '16'},
                {text: 'I love REACT!!!', like: '45'},
            ],
            newPostText: 'it-sphere'
        },

        friendsPage: {
            friendsData: [
                {username: 'Evgeniy1'},
                {username: 'Evgeniy2'},
                {username: 'Evgeniy3'},
            ],
        },
    };

    window.state = state;


     const addPost = () => {

        let newPost = {
            name: 'Oleg',
            text: state.profilePage.newPostText,
            like: '56',
        };
        state.profilePage.postsData.push(newPost);
        state.profilePage.newPostText = '';
        rerenderEntireTree();
    };

     const newTextUpdate = (newText) => {
        state.profilePage.newPostText = newText;
        rerenderEntireTree();
    };

     const subscribe = (obs) => {
        rerenderEntireTree = obs;
    }
}





export default store;
