import ums from './Users.module.css'
function Users(props) {

    // let state = props.usersPage
    if (props.users.length === 0 ) {
        props.setUsers([
/*            {
                id: 32,
                followed: false,
                name: 'Dimych',
                location: {country: 'Russia', city: 'Moscow'},
                descriptionProfile: 'Сегодня ты делаешь код, завтра код делает тебе деньги',
                avaUrl: 'https://p4.tabor.ru/feed/2018-09-24/15254420/1139903_760x500.jpg',
            },
            {
                id: 33,
                followed: true,
                name: 'Evgeniy',
                location: {country: 'Belarus', city: 'Minsk'},
                descriptionProfile: 'Настоящий программист гораздо больше читает, чем пишет',
                avaUrl: 'https://p4.tabor.ru/feed/2018-09-24/15254420/1139903_760x500.jpg',
            },
            {
                id: 34,
                followed: true,
                name: 'Sasha',
                location: {country: 'Kazakhstan', city: 'Almaty'},
                descriptionProfile: 'Идеальному коду место в музее: там ценят всякие древности',
                avaUrl: 'https://p4.tabor.ru/feed/2018-09-24/15254420/1139903_760x500.jpg',
            },
            {
                id: 435,
                followed: false,
                name: 'Nikita',
                location: {country: 'Uzbekistan', city: 'Tashkent'},
                descriptionProfile: 'Можно сделать довольно большой проект, вообще не прикасаясь к мыши. Но зачем?',
                avaUrl: 'https://p4.tabor.ru/feed/2018-09-24/15254420/1139903_760x500.jpg',
            }*/
            ]
        )
    }
    return <div>
        {
        props.users.map(u => <div key={u.id}>

            {/*// for avatar and button "follow"*/}
            <span>
                <div> <img src={u.avaUrl} className={ums.ava_photo} alt={"profile avatar"}/> </div>
                <div>{ u.followed
                    ?<button onClick={ () => {props.followUID(u.id) } } > Follow </button>
                    :<button onClick={ () => {props.unfollowUID(u.id) } }> Unfollow </button>}
                </div>
            </span>

            {/*for name, location and status*/}
            <span>
                {/* for name and status*/}
                <span>
                    <div> {u.name} </div>
                    <div> {u.descriptionProfile} </div>
                </span>

                 {/*for locations*/}
                <span>
                    <div>{u.location.city}</div>
                    <div>{u.location.country}</div>
                </span>

            </span>
        </div>)
    }</div>
}
export default Users;
