const ADD_POST  = 'ADD-POST';
const NEW_TEXT_UPDATE  = 'NEW-TEXT-UPDATE';

let initialState = {
    postsData: [
        {name: 'Evgeniy', text: 'I need more React', like: '16'},
        {text: 'I love REACT!!!', like: '45'},
    ],
    newPostText: 'it-sphere'
};

const profileReducer = (state = initialState, action) => {

    switch (action.type) {
        case ADD_POST: {
            let newPost = {
                name: 'Oleg',
                text: state.newPostText,
                like: '56',
            };
            let stateCopy = {...state}
            stateCopy.posts = [...state.posts];
            stateCopy.posts.push(newPost)
            stateCopy.newPostText = '';
            return stateCopy
            break;
        }
        case NEW_TEXT_UPDATE: {
            let stateCopy = {...state}
            stateCopy.newPostText = [...state.newPostText]
            stateCopy.newPostText = action.newText;
            return stateCopy
            break;
        }
        default:
            return state
            break;
    }
}

export const addPostActionCreater = () => {
    return {
        type: ADD_POST,
    }
}
export const updPostActionCreater = (textPost) => {
    return {
        type: NEW_TEXT_UPDATE,
        newText: textPost,
    }
}


export default profileReducer;
