import posts from '../Posts/MyPosts.module.css';
import Wall from './Post/Post.jsx';
import {createRef} from "react";
import {addPostActionCreater, updPostActionCreater} from "../../../redux/state";

function MyPosts(props) {

    let postsElements =
        props.postsData.map(p => <Wall name={p.name} text={p.text} likeCount={p.like}/>)

    let newMessageElement = createRef();
    let addPost = () => {
        props.dispatch(addPostActionCreater());
        newMessageElement.current.value = '';
    };

    let onPostChange = () => {
        let textPost = newMessageElement.current.value;
        let action = updPostActionCreater(textPost);
        props.dispatch(action);
    }
    return (
        <div className={posts.postsBlock}>
            <div className={posts.myP}>
                <h3>MyPosts</h3>
            </div>
            <div>
                <textarea onChange={onPostChange} ref={newMessageElement} value={props.newPostText}/>
            </div>
            <div>
                <button onClick={addPost}>Add post</button>
            </div>
            <div className={posts.wallPost}>
                {postsElements}
            </div>
        </div>
    );
}

export default MyPosts;
