import ums from './Users.module.css'
import axios from "axios";
import userPhoto from "../../img/avatar.jpg"
import {useEffect} from "react";
import classNames from "classnames"

function Users(props) {

    useEffect(() => {
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${props.currentPage}&count=${props.pageSize}`)
            .then(response => {
                props.setUsers(response.data.items);
                props.setTotalUsersCount(response.data.totalCount);
            });
    }, [])

    let pageCount = Math.ceil(props.totalUsersCount / props.pageSize)
    let pages = [];
    for (let i = 1; i <= pageCount; i++) {
        if (pages.length < 10) {
            pages.push(i);
        }
    }

    let onPageChanged = (pageNumber) => {
        props.setCurrentPage(pageNumber);
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${props.pageSize}`)
            .then(response => {
                props.setUsers(response.data.items)
            });

    }

    return <div>
        <div className={ums.num_bar}>
            {pages.map(p => <span className={props.currentPage === p && ums.selected}
                                  onClick={() => {onPageChanged(p);}}> {p} </span>)}
        </div>

        {props.users.map(u => <div className={ums.profile} key={u.id}>

            {/*// for avatar and button "follow"*/}
            <span>
                <div> <img src={u.photos.small != null ? u.photos.small : userPhoto} className={ums.ava_photo}
                           alt={"profile avatar"}/> </div>
                <div>{u.followed ? <button onClick={() => {
                    props.followUID(u.id)
                }}> Follow </button> : <button onClick={() => {
                    props.unfollowUID(u.id)
                }}> Unfollow </button>}
                </div>
            </span>

            {/*for name, location and status*/}
            <span>
                {/* for name and status*/}
                <span>
                    <div className={ums.profile_name}> {u.name} </div>
                    <div className={ums.description}> {u.status} </div>
                </span>

                {/*for locations*/}


            </span>
        </div>)}</div>
}

export default Users;
