import {SendMessageActionCreater, UpdateNewMessageActionCreater} from "../../redux/dialogsReducer";
import Dialogs from "./Dialogs";
import StoreContext from "../../StoreContext";

function DialogsContainer() {

    return (
        <StoreContext.Consumer>
            {(store) => {
                let state = store.getState().dialogsPage;
                let sendMessage = () => {
                    store.dispatch(SendMessageActionCreater())
                }
                let OnNewMessageSend = (body) => {
                    store.dispatch(UpdateNewMessageActionCreater(body))
                }

                return <Dialogs OnNewMessageSend={OnNewMessageSend} sendMessage={sendMessage}
                                dialogsPage={state}/>
            }}
        </StoreContext.Consumer>);
}


export default DialogsContainer;
