function Users(props) {
     let state = props.usersPage
    props.setUsersAc
    return <div>{
        state.users.map(u => <div key={u.id}>

            <span>
                <div> <img src={u.avaUrl} alt={"profile avatar"}/> </div>
                <div>{ u.followed
                    ?<button onClick={ () => {props.followed(u.id) } } > Follow </button>
                    :<button onClick={ () => {props.unfollow(u.id) } }> Unfollow </button>}
                </div>
            </span>

            {/*for name, location and status*/}
            <span>
                {/* for name and status*/}
                <span>
                    <div> {u.name} </div>
                    <div> {u.descriptionProfile} </div>
                </span>

                 {/*for locations*/}
                <span>
                    <div>{u.location.city}</div>
                    <div>{u.location.country}</div>
                </span>
            </span>

        </div>)
    }</div>
}

export default Users;
