import './App.module.css';
import s from './App.module.css'
import Nav from "./compo/Nav/Nav";
import {BrowserRouter, Route, Routes} from 'react-router-dom';
import ScrollToTop from "react-scroll-to-top";
import UsersContainer from "./compo/Users/UsersContainer";
import ProfileContainer from "./compo/Profile/ProfileContainer";
import HeaderContainer from "./compo/Header/HeaderComponent";
import Dialogs from "./compo/Message/Dialogs";
import Login from "./compo/Login/Login";
import {useDispatch, useSelector} from "react-redux";
import {getIsAuth, getLogin} from "./redux/authSelector";
import {loginMeThunk, logoutThunk, setAuthUserData} from "./redux/authReducer";
import {useEffect} from "react";
import {initializeApp} from "./redux/appReducer";


function App(props) {

    let loginState = useSelector(state => getLogin(state))
    let isAuthState = useSelector(state => getIsAuth(state))

    const dispatch = useDispatch()
    let userData = () => dispatch(initializeApp)
    useEffect(() => {
        userData()
    }, [dispatch, userData])


    let logout = () => dispatch(logoutThunk)

    return (<BrowserRouter>
            <div className={s.grid}>
                <HeaderContainer/>
                <Nav/>
                <ScrollToTop smooth/>
                <div className={s.content}>
                    <Routes>
                        <Route path="/dialogs/*" element={<Dialogs/>}/>
                        <Route path="/profile/:userId" element={<ProfileContainer/>}/>
                        <Route path='/profile/24394' element={<ProfileContainer/>}/>
                        <Route path="/users" element={<UsersContainer/>}/>
                        <Route path="/login" element={<Login/>}/>
                    </Routes>
                </div>
            </div>
        </BrowserRouter>);
}

export default App;
