import i from './ProfileInfo.module.css';
import {useState} from "react";

const ProfileStatus = (props) => {

    let state = { editMode: false }

    let [status, setStatus] = useState()
    let [view, setToggleView] = useState()

    return (
        <div>
            {!state.editMode &&
            <span onDoubleClick={ () => {state.editMode = true} } className={i.info_profile}>Status:
                <span className={i.desc_txt}>
                    {props.profile.status}
                </span>
            </span>
            }
            {state.editMode &&
            <div>
                <input type={"text"} value={props.profile.status}/>
            </div>
            }
        </div>
    )
}

export default ProfileStatus;
