import axios from "axios";

export const getUsers = (props) => {
    return axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${props.pageSize}`, {withCredentials: true})
        .then(response => {
            props.switchIsFetching(false);
            props.setUsers(response.data.items)
        });
}
