/*import {rerenderEntireTree} from "../index"; */

let store  = {
    _state: {
        dialogsPage: {
            dialogsData: [
                {id: 1, name: "Dimych",},
                {
                    id: 2,
                    ava: 'https://avatars.mds.yandex.net/i?id=a7190ac7b0d5cc3862b9e0113bebd4d8-4318341-images-thumbs&n=13',
                    name: "Evgeniy",
                },
                {
                    id: 3,
                    ava: 'https://avatars.mds.yandex.net/i?id=a7190ac7b0d5cc3862b9e0113bebd4d8-4318341-images-thumbs&n=13',
                    name: "Sasha",
                },
            ],
            newMessageText: 'YoYoYo',
            messagesData: [
                {
                    id: 1,
                    ava: "https://avatars.mds.yandex.net/i?id=a7190ac7b0d5cc3862b9e0113bebd4d8-4318341-images-thumbs&n=13",
                    message: "Hello",
                },
                {
                    id: 2,
                    ava: "https://avatars.mds.yandex.net/i?id=a7190ac7b0d5cc3862b9e0113bebd4d8-4318341-images-thumbs&n=13",
                    message: "How are you?",
                },
                {
                    id: 3,
                    ava: "https://avatars.mds.yandex.net/i?id=a7190ac7b0d5cc3862b9e0113bebd4d8-4318341-images-thumbs&n=13",
                    message: "Chel",
                },
            ],
        },
        profilePage: {
            postsData: [
                {name: 'Evgeniy', text: 'I need more React', like: '16'},
                {text: 'I love REACT!!!', like: '45'},
            ],
            newPostText: 'it-sphere'
        },
        friendsPage: {
            friendsData: [
                {username: 'Evgeniy1'},
                {username: 'Evgeniy2'},
                {username: 'Evgeniy3'},
            ],
        },
    },
    _rerenderEntireTree() {
        console.log('it is it')
    },

    getState() {return this._state},
    subscribe(obs) {
        this._rerenderEntireTree = obs;
    },

/*    addPost() {
        let newPost = {
            name: 'Oleg',
            text: this._state.profilePage.newPostText,
            like: '56',
        };
        this._state.profilePage.postsData.push(newPost);
        this._state.profilePage.newPostText = '';
        this._rerenderEntireTree();
    },*/
    newTextUpdate(newText) {
        this._state.profilePage.newPostText = newText;
        this._rerenderEntireTree();
    },
    newMesTextUpdate(newMesText) {
        this._state.dialogsPage.newMessageText = newMesText;
        this._rerenderEntireTree();
    },
    dispatch(action) {
        if (action.type === 'ADD-POST') {
            let newPost = {
                name: 'Oleg',
                text: this._state.profilePage.newPostText,
                like: '56',
            };
            this._state.profilePage.postsData.push(newPost);
            this._state.profilePage.newPostText = '';
            this._rerenderEntireTree();
        }
        else if (action.type === 'NEW-TEXT-UPDATE') {
            this._state.profilePage.newPostText = action.newText;
            this._rerenderEntireTree();
        }
        else if (action.type === 'SEND-MESSAGE') {
            let newMessage = {
                id: '4',
                text: this._state.dialogsPage.newMessageText,
            };
            this._state.dialogsPage.messagesData.push(newMessage);
            this._state.dialogsPage.newMessageText = '';
            this._rerenderEntireTree();
        }
        else if (action.type === 'NEW-MESSAGE-UPDATE') {
            this._state.dialogsPage.newMessageText = action.newMesText;
            this._rerenderEntireTree();
        }
    },

};

export const addPostActionCreater = () => {
    return {
        type: 'ADD-POST',
    }
}

export const updPostActionCreater = (textPost) => {
    return {
        type: 'NEW-TEXT-UPDATE',
        newText: textPost,
    }
}

export const sendMessageActionCreater = () => {
    return {
        type: 'SEND-MESSAGE',
    }
}

export const updMessageActionCreater = (textMessage) => {
    return {
        type: 'NEW-MESSAGE-UPDATE',
        newMesText: textMessage,
    }
}

window.store = store;

export default store;
